(11, 'Máy bơm nước Panasonic 125W A-130JAK', 'may-bom-nuoc-panasonic-125w-a-130jak', 1, 'images/uploads/images/panasonic/panasonic_1.jpg;images/uploads/images/panasonic/panasonic_1.jpg', '', '<p>\r\n	Máy bơm nước Panasonic 125W A-130JAK\r\n</p>', '<h2 style="text-align: justify;">\r\n	<span style="font-size:24px;">Máy bơm nước Panasonic 125W A-130JAK</span>\r\n</h2>\r\n\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size: 12pt; color: #0000ff;">Ứng dụng của máy bơm tự động tăng áp&nbsp;Panasonic&nbsp;A-130JAK:</span>\r\n</h2>\r\n\r\n<p style="text-align: justify;">\r\n	<span style="font-size: 12pt;">&ndash; Tăng áp lực nước từ bồn chứa nước xuống các tầng lầu, tự động đóng ngắt khi đóng mở van nước.</span>\r\n</p>\r\n\r\n<p style="text-align: justify;">\r\n	<span style="font-size: 12pt;">&ndash; Hút trực tiếp nước từ đường ống cấp nước để tăng áp lực cho các thiết bị sử trong gia đình như máy giặt, sen, vòi, bình nóng lạnh,&hellip;</span>\r\n</p>\r\n\r\n<p style="text-align: justify;">\r\n	<span style="font-size: 12pt;">&ndash;&nbsp;Thích hợp cho các căn hộ tập thể, chung cư, hộ gia đình có&nbsp;các bồn chứa nước ở vị trí thấp, áp lực nước nhỏ, máy sẽ tự động tăng áp lực nước khi bơm ra.</span>\r\n</p>\r\n\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size: 12pt; color: #0000ff;">Thiết kế của của&nbsp;máy bơm tự động tăng áp&nbsp;Panasonic A-130JAK:</span>\r\n</h2>\r\n\r\n<p style="text-align: justify;">\r\n	<span style="font-size: 12pt;">&ndash; Thân mô tơ bằng nhôm đúc, cánh bằng đồng.</span><br>\r\n	<span style="font-size: 12pt;">&ndash; Bầu tăng áp lực bằng cao su Butyl xếp theo nếp nên chắc chắn và bền bỉ.</span><br>\r\n	<span style="font-size: 12pt;">&ndash; Khí ni tơ bên trong buồng tích trữ cho máy hoạt động ổn định một cách tối ưu.</span><br>\r\n	<span style="font-size: 12pt;">&ndash; Phốt chịu mài mòn cao, cốt máy bằng inox làm thiểu sự ma sát giúp tăng tuổi thọ và độ bền của máy.</span><br>\r\n	<span style="font-size: 12pt;">&ndash; Hai lớp&nbsp; bọc cách ly bảo vệ lõi tĩnh mô tơ, chống ăn mòn, giảm thiểu nguy cơ chạm điện.</span><br>\r\n	<span style="font-size: 12pt;">&ndash; Công tắc tự động được tích hợp bên trong bộ bảo vệ bằng vật liệu chống cháy, cới tiếp điểm bạch kim siêu bề, có chức năng mở tắt nguồn khi dòng nước qua máy bơm bị khóa chặn.</span><br>\r\n	<span style="font-size: 12pt;">&ndash;&nbsp;Bên trong tích hợp bị rờ le nhiệt, giúp máy tự động tắt khi nhiệt độ tăng quá nóng để bảo vệ động cơ và tăng tuổi thọ sử dụng.</span><br>\r\n	<span style="font-size: 12pt;">&ndash; Luồng nước bơm có thể kiểm soát nhờ công tắc tự động liên kết với vòi khóa nước, rất tiện ích.</span>\r\n</p>', NULL, 'Máy bơm nước Panasonic 125W A-130JAK', 'Máy bơm nước Panasonic 125W A-130JAK', 'Máy bơm nước Panasonic 125W A-130JAK', '0', 0, '0', 1, 1, 1, 62, '2018-06-04 04:15:42', '2018-06-06 09:13:05'),
(12, 'Máy bơm nước Panasonic 125W A-130JTX', 'may-bom-nuoc-panasonic-125w-a-130jtx', 1, 'images/uploads/images/panasonic/panasonic_2.jpg;images/uploads/images/panasonic/panasonic_2.jpg', '', '<p>\r\n	Máy bơm nước Panasonic 125W A-130JTX\r\n</p>', '<h2>\r\n	<span style="font-size:20px;">Máy bơm nước Panasonic 125W A-130JTX</span>\r\n</h2>\r\n\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size: 12pt; color: #0000ff;">Ứng dụng của máy bơm tự động tăng áp&nbsp;Panasonic&nbsp;A-130JTX:</span>\r\n</h2>\r\n\r\n<p style="text-align: justify;">\r\n	<span style="font-size: 12pt;">&ndash;&nbsp;Sử dụng để bơm, vận chuyển nước từ mặt đất lên bồn chứa nước trên nóc các tòa nhà.</span>\r\n</p>\r\n\r\n<p style="text-align: justify;">\r\n	<span style="font-size: 12pt;">&ndash;&nbsp;Hút trực tiếp nước từ đường ống cấp nước để tăng áp lực cho các thiết bị sử trong gia đình như máy giặt, sen, vòi, bình nóng lạnh,&hellip;</span>\r\n</p>\r\n\r\n<p style="text-align: justify;">\r\n	<span style="font-size: 12pt;">&ndash;&nbsp;Thích hợp cho các căn hộ tập thể, chung cư, hộ gia đình có vị trí lắp đặt máy bơm không có mái che.</span>\r\n</p>\r\n\r\n<p style="text-align: justify;">\r\n	<span style="font-size: 12pt;">&ndash; Tính năng tạo bọt khí phù hợp tăng áp cho bồn spa hoặc jacuzzi, massage&hellip;</span>\r\n</p>\r\n\r\n<p style="text-align: center;">\r\n	<img alt="" class="size-medium wp-image-707 aligncenter" sizes="(max-width: 300px) 100vw, 300px" src="http://diencongocduong.com/wp-content/uploads/2017/12/Máy-bơm-tăng-áp-Panasonic-A-130JTX-300x300.gif" srcset="http://diencongocduong.com/wp-content/uploads/2017/12/Máy-bơm-tăng-áp-Panasonic-A-130JTX-300x300.gif 300w, http://diencongocduong.com/wp-content/uploads/2017/12/Máy-bơm-tăng-áp-Panasonic-A-130JTX-150x150.gif 150w, http://diencongocduong.com/wp-content/uploads/2017/12/Máy-bơm-tăng-áp-Panasonic-A-130JTX-180x180.gif 180w" style="width: 50%; height: 50%;">\r\n</p>\r\n\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size: 12pt; color: #0000ff;">Thiết kế của của&nbsp;máy bơm tự động&nbsp;tăng áp Panasonic A-130JTX:</span>\r\n</h2>\r\n\r\n<p style="text-align: justify;">\r\n	<span style="font-size: 12pt;">&ndash; Thân mô tơ bằng nhôm đúc, cánh bằng đồng.</span>\r\n</p>\r\n\r\n<p style="text-align: justify;">\r\n	<span style="font-size: 12pt;">&ndash; Bầu tăng áp lực bằng cao su Butyl xếp theo nếp nên chắc chắn và bền bỉ.</span>\r\n</p>\r\n\r\n<p style="text-align: justify;">\r\n	<span style="font-size: 12pt;">&ndash; Khí ni tơ bên trong buồng tích trữ cho máy hoạt động ổn định một cách tối ưu.</span>\r\n</p>\r\n\r\n<p style="text-align: justify;">\r\n	<span style="font-size: 12pt;">&ndash; Phốt chịu mài mòn cao, cốt máy bằng inox làm thiểu sự ma sát giúp tăng tuổi thọ và độ bền của máy.</span>\r\n</p>\r\n\r\n<p style="text-align: justify;">\r\n	<span style="font-size: 12pt;">&ndash; Hai lớp&nbsp; bọc cách ly bảo vệ lõi tĩnh mô tơ, chống ăn mòn, giảm thiểu nguy cơ chạm điện.</span>\r\n</p>\r\n\r\n<p style="text-align: justify;">\r\n	<span style="font-size: 12pt;">&ndash; Công tắc tự động được tích hợp bên trong bộ bảo vệ bằng vật liệu chống cháy, cới tiếp điểm bạch kim siêu bề, có chức năng mở tắt nguồn khi dòng nước qua máy bơm bị khóa chặn.</span>\r\n</p>\r\n\r\n<p style="text-align: justify;">\r\n	<span style="font-size: 12pt;">&ndash;&nbsp;Bên trong tích hợp bị rờ le nhiệt, giúp máy tự động tắt khi nhiệt độ tăng quá nóng để bảo vệ động cơ và tăng tuổi thọ sử dụng</span>\r\n</p>\r\n\r\n<p style="text-align: justify;">\r\n	<span style="font-size: 12pt;">&ndash; Được thiết kế có thêm nắp đậy bằng nhựa siêu bền chịu mưa nắng, sản phẩm được thiết kế sử dụng ngoài trời.&nbsp;&ndash; Có tính năng tạo bọt khí.</span>\r\n</p>\r\n\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size: 12pt; color: #0000ff;">Thông số kỹ thuật máy bơm tự động tăng áp Panasonic A-130JTX:</span>\r\n</h2>\r\n\r\n<p style="text-align: center;">\r\n	<img alt="" src="http://localhost:8080/maybomnuoc/images/uploads/images/tintuc/Panasonic-A-130JTX-1.jpg" style="width: 80%; height: 80%;">\r\n</p>\r\n\r\n<p>\r\n	&nbsp;\r\n</p>', NULL, 'Máy bơm nước Panasonic 125W A-130JTX', 'Máy bơm nước Panasonic 125W A-130JTX', 'Máy bơm nước Panasonic 125W A-130JTX', '0', 0, '0', 0, 1, 1, 62, '2018-06-04 04:15:42', '2018-06-06 09:27:00'),
