(53, 'Bơm CM Công Suất Lớn (cáng gang)', 'bom-cm-cong-suat-lon-cang-gang', 1, 'images/uploads/images/mepcatocn/mapcato_cn_1.jpg;images/uploads/images/mepcatocn/mapcato_cn_1.jpg', '', '<p>\r\n	Bơm CM Công Suất Lớn (cáng gang)\r\n</p>', '<p>\r\n	<span style="font-size: large;"><strong>ÁP DỤNG:</strong></span>\r\n</p>\r\n\r\n<ul style="margin-left: 40px;">\r\n	<li>\r\n		<span style="font-size: medium;">Bơm cung cấp nước trong ngành Công nghiệp và Nông nghiệp.</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Bơm hệ thống xử lý nước sạch cho các khu công nghiệp và chung cư.</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Bơm hệ thống tưới tiêu thủy lợi.</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Bơm cứu hoả, phòng cháy chữa cháy.</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Bơm nước sinh hoạt cho nhà cao tầng.</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style="font-size: large;"><strong>ĐẶC TÍNH:</strong></span>\r\n</p>\r\n\r\n<ul style="margin-left: 40px;">\r\n	<li>\r\n		<span style="font-size: medium;">Đầu bơm đúc bằng gang</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Dây điện quấn bằng đồng</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Cánh bơm đồng thau</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Trục bơm bằng inox 304 hoặc 316</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Phốt Italy</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Bạc đạn Nhật Nachi</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style="font-size: medium;"><strong>XUẤT XỨ: LIÊN DOANH </strong></span>\r\n</p>\r\n\r\n<p>\r\n	<span style="font-size: medium;"><strong>CÔNG NGHỆ: ITALY</strong></span>\r\n</p>\r\n\r\n<p>\r\n	<span style="font-size: medium;"><strong><img alt="" src="http://localhost:8080/maybomnuoc/images/uploads/images/tintuc/BOSINI.jpg" style="width: 100%; height: 100%;"></strong></span>\r\n</p>', NULL, 'Bơm CM Công Suất Lớn (cáng gang)', 'Bơm CM Công Suất Lớn (cáng gang)', 'Bơm CM Công Suất Lớn (cáng gang)', '0', 0, '0', 0, 1, 1, 68, '2018-06-04 04:15:42', '2018-06-06 08:09:08'),
(54, 'Bơm Trục Đứng Inox CDLF', 'bom-truc-dung-inox-cdlf', 1, 'images/uploads/images/cnp/cnp_1.jpg;images/uploads/images/cnp/cnp_1.jpg', '', '<p>\r\n	Bơm Trục Đứng Inox CDLF\r\n</p>', '<p>\r\n	<span style="font-size: medium;"><strong>Bơm</strong></span>\r\n</p>\r\n\r\n<ul style="margin-left: 40px;">\r\n	<li>\r\n		<span style="font-size: medium;">CDL(F) là loại bơm ly tâm đa tầng trục đứng không tự mồi, chạy bằng motor. Trục motor nối thẳng vào trục bơm qua một khớp nối. Vỏ bơm chịu lực và các bộ phận cho chất lỏng chạy qua được gắn giữa đầu bơm và phần cửa ra &amp; cửa vào bằng then tie-bar. Cửa ra và cửa vào đồng trục và nằm ở dưới. Loại bơm này có thể trang bị bộ phận bảo vệ thông minh để ngăn chặn hiệu quả việc chạy khô, mất pha và chạy quá tải.</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style="font-size: medium;">&nbsp;<strong>Motor</strong></span>\r\n</p>\r\n\r\n<ul style="margin-left: 40px;">\r\n	<li>\r\n		<span style="font-size: medium;">Motor tiêu chuẩn hai cực bọc kín</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Lớp bảo vệ : IP 55</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Lớp cách điện : F</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Điện áp tiêu chuẩn 1 x 220 &ndash; 230 / 240V<br>\r\n		3 x 200 &ndash; 220 / 346-380V<br>\r\n		3 x 220 &ndash; 240 / 380-415 V<br>\r\n		3 x 380 &ndash; 415 V</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style="font-size: medium;"><strong>Áp dụng</strong></span>\r\n</p>\r\n\r\n<p>\r\n	<span style="font-size: medium;">CDL(F)&nbsp;là một loại sản phẩm đa chức năng. Nó có thể dùng để tải các chất lỏng khác nhau từ nước máy tới chất lỏng công nghiệp ở nhiệt độ khác nhau và với áp và lưu lượng khác nhau. Loại CDL được dùng để tải các chất lỏng không ăn mòn ( phần cửa ra &amp; vào làm bằng gang ), trong khi đó CDLF thì thích hợp cho các chất lỏng hơi ăn mòn ( cửa ra &amp; vào làm bằng thép không gỉ SS 304 hoặc SS 316 ).</span>\r\n</p>\r\n\r\n<ul style="margin-left: 40px;">\r\n	<li>\r\n		<span style="font-size: medium;">Bơm nước : lọc nước và bơm nước trong các công trình cấp nước, tăng áp cho đường ống chính, tăng áp cho các nhà cao tầng.</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Tăng áp công nghiệp : xử lý hệ thống nước chảy, hệ thống làm sạch, hệ thống rửa áp lực cao, hệ thống chữa cháy.</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Tải chất lỏng công nghiệp : hệ thống làm lạnh và điều hòa không khí, cung cấp nước cho nồi hơi và hệ thống ngưng tụ, cho các mục đích liên quan đến máy móc, acid và alkali.</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Xử lý nước : hệ thống lọc nước, hệ thống RO, hệ thống chưng cất, hồ bơi.</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Tưới : tưới trang trại, tưới phun, tưới nhỏ giọt.</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style="font-size: medium;"><strong>Điều kiện hoạt động</strong></span>\r\n</p>\r\n\r\n<ul style="margin-left: 40px;">\r\n	<li>\r\n		<span style="font-size: medium;">Các chất lỏng không gây cháy, nổ, sạch, không chứa các hạt rắn và chất xơ sợi</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Nhiệt độ chất lỏng :<br>\r\n		- Loại nhiệt độ bình thường : -15<sup>0</sup>C&nbsp; đến&nbsp; 70<sup>0</sup>C<br>\r\n		- Loại nước nóng : +70<sup>0</sup>C đến + 120<sup>0</sup>C</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Nhiệt độ môi trường : lên đến + 40<sup>0</sup>C</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Độ cao : lên đến 1000m</span>\r\n	</li>\r\n</ul>', NULL, 'Bơm Trục Đứng Inox CDLF', 'Bơm Trục Đứng Inox CDLF', 'Bơm Trục Đứng Inox CDLF', '0', 0, '0', 0, 1, 1, 69, '2018-06-04 04:15:42', '2018-06-06 08:10:42'),
(55, 'Bơm Trục Ngang Inox MS', 'bom-truc-ngang-inox-ms', 1, 'images/uploads/images/cnp/cnp_2.jpg;images/uploads/images/cnp/cnp_2.jpg', '', '<p>\r\n	Bơm Trục Ngang Inox MS\r\n</p>', '<p>\r\n	<span style="font-size: medium;"><strong>CẤU TRÚC</strong></span>\r\n</p>\r\n\r\n<ul style="margin-left: 40px;">\r\n	<li>\r\n		<span style="font-size: medium;">Bơm serie MS là loại bơm ly tâm đơn tầng với đặc tính hút xuyên tâm và xả tỏa tâm</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Cấu trúc gọn. Bơm gắn trực tiếp với motor.</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Lắp đặt thuận tiện, cửa vào và cửa ra được ren răng.</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Nhẹ, theo cấu trúc dập các miếng kim loại cho các phần chính và bộ phận.</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Có tính kháng mòn, các bộ phận mà dòng chảy chảy qua sử dụng thép không gỉ AISI 304 và AISI 316.</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style="font-size: medium;"><strong>ÁP DỤNG</strong></span>\r\n</p>\r\n\r\n<ul style="margin-left: 40px;">\r\n	<li>\r\n		<span style="font-size: medium;">Tạo áp lực và bơm nước sạch công nghiệp và dân dụng hoặc các chất lỏng khác.</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Xử lý nước.</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Hệ thống luân chuyển nước</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Tưới nông nghiệp</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Các lãnh vực khác</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style="font-size: medium;"><strong>ĐIỀU KIỆN HOẠT ĐỘNG</strong></span>\r\n</p>\r\n\r\n<ul style="margin-left: 40px;">\r\n	<li>\r\n		<span style="font-size: medium;">Nhiệt độ chất lỏng : - 10<sup>0</sup>C đến + 85<sup>0</sup>C</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Nhiệt độ môi trường : lên đến + 40<sup>0</sup>C</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Độ cao : lên đến 1000m</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Áp lực tối đa của hệ thống : 8 bar</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style="font-size: medium;"><strong>MOTOR</strong></span>\r\n</p>\r\n\r\n<ul style="margin-left: 40px;">\r\n	<li>\r\n		<span style="font-size: medium;">TEFC motor hai cực</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Lớp bảo vệ : IP 55</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Lớp cách điện : F</span>\r\n	</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>\r\n		<span style="font-size: medium;">Điện áp tiêu chuẩn 50 Hz : 1 x 220V<br>\r\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3 x 380V/3 x 220V</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style="font-size: medium;"><strong>YÊU CẦU LẮP ĐẶT</strong></span>\r\n</p>\r\n\r\n<ul style="margin-left: 40px;">\r\n	<li>\r\n		<span style="font-size: medium;">Máy bơm phải được buộc chặt trên một đế nằm ngang vững vàng</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Việc lắp đặt phải đảm bảo rằng máy bơm không bị ảnh hưởng bởi sức căng của hệ thống đường ống.</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Thiết bị điện cũng phải đảm bảo rằng máy bơm không bị hư hại bởi việc mất pha, điện áp không ổn định, hở mạch và quá tải.</span>\r\n	</li>\r\n</ul>', NULL, 'Bơm Trục Ngang Inox MS', 'Bơm Trục Ngang Inox MS', 'Bơm Trục Ngang Inox MS', '0', 0, '0', 0, 1, 1, 69, '2018-06-04 04:15:42', '2018-06-06 08:28:31'),
(56, 'Bơm Đa Tầng Cánh Inox CHL', 'bom-da-tang-canh-inox-chl', 1, 'images/uploads/images/cnp/cnp_3.jpg;images/uploads/images/cnp/cnp_3.jpg', '', '<p>\r\n	Bơm Đa Tầng Cánh Inox CHL\r\n</p>', '<p>\r\n	<span style="font-size: medium;"><strong>ÁP DỤNG</strong></span>\r\n</p>\r\n\r\n<p>\r\n	<span style="font-size: medium;">Loại CHL, chủ yếu dùng trong lãnh vực công nghiệp</span>\r\n</p>\r\n\r\n<ul style="margin-left: 40px;">\r\n	<li>\r\n		<span style="font-size: medium;">Hệ thống điều hòa không khí</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Hệ thống làm lạnh</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Làm sạch công nghiệp</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Xử lý nước ( lọc nước )</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Nuôi trồng thủy sản</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Phân bón</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style="font-size: medium;"><strong>CHẤT LỎNG CÓ THỂ SỬ DỤNG</strong></span>\r\n</p>\r\n\r\n<ul style="margin-left: 40px;">\r\n	<li>\r\n		<span style="font-size: medium;">Các chất lỏng không gây cháy, nổ, sạch, không chứa các hạt rắn và các chất xơ sợi</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Nước khoáng, nước mềm, nước tinh khiết, dầu thực vật ăn được và các hoá chất nhẹ khác.</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Khi chất lỏng cần bơm có độ đậm đặc và độ nhớt lớn&nbsp; hơn nước, nhất thiết phải dùng motor có công suất cao hơn.</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Một chất lỏng cụ thể có thích hợp cho bơm hay không tuỳ thuộc vào nhiều yếu tố, trong đó các yếu tố quan trọng nhất là hàm lượng chlorine, giá trị PH, nhiệt độ, dung môi và hàm lượng dầu.</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style="font-size: medium;"><strong>BƠM</strong></span>\r\n</p>\r\n\r\n<ul style="margin-left: 40px;">\r\n	<li>\r\n		<span style="font-size: medium;">Bơm ly tâm đa tầng trục ngang không tự mồi, gắn với motor trục dài</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Cấu trúc gọn làm cho bơm có kích thước nhỏ.</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style="font-size: medium;"><strong>MOTOR</strong></span>\r\n</p>\r\n\r\n<ul style="margin-left: 40px;">\r\n	<li>\r\n		<span style="font-size: medium;">TEFC motor hai cực</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Lớp bảo vệ : IP 55</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Lớp cách điện : F</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Điện áp tiêu chuẩn 50 Hz : 1 x 220-240V<br>\r\n		3 x 220-240V/380-415V</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style="font-size: medium;"><strong>ĐIỀU KIỆN HOẠT ĐỘNG</strong></span>\r\n</p>\r\n\r\n<ul style="margin-left: 40px;">\r\n	<li>\r\n		<span style="font-size: medium;">Nhiệt độ chất lỏng :<br>\r\n		- Loại nhiệt độ bình thường : -15<sup>0</sup>C đến + 70<sup>0</sup>C<br>\r\n		- Loại nước nóng : + 70<sup>0</sup>C đến + 110<sup>0</sup>C</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Nhiệt độ môi trường : lên đến + 40<sup>0</sup>C</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Áp lực làm việc tối đa : 10 bar</span>\r\n	</li>\r\n	<li>\r\n		<span style="font-size: medium;">Áp lực làm việc tối đa tại đầu vào bị giới hạn bởi áp lực làm việc tối đa.</span>\r\n	</li>\r\n</ul>', NULL, 'Bơm Đa Tầng Cánh Inox CHL', 'Bơm Đa Tầng Cánh Inox CHL', 'Bơm Đa Tầng Cánh Inox CHL', '0', 0, '0', 0, 1, 1, 69, '2018-06-04 04:15:42', '2018-06-06 08:29:40'),
